# COMMAND ----------

Develop_path = generate_path('raw-develop-delta2024','silverlayer')

Transform_Data = generate_path('transform-delta2018','silverlayer')

df_develop = spark.read.format("delta").load(Develop_path)


# COMMAND ----------

# Display the expanded DataFrame
display(df_develop)

# COMMAND ----------

from pyspark.sql.functions import explode, split

# Convert 'elementList' from STRING to ARRAY
df_develop = df_develop.withColumn("elementList", split(df_develop["elementList"], ","))

# Expand 'elementList' in 'df_develop' DataFrame
df_develop_expanded = df_develop.withColumn("element", explode("elementList"))

# Display the expanded DataFrame
display(df_develop_expanded)

# COMMAND ----------

from pyspark.sql.functions import col

# Get distinct count for each column in df_develop_expanded
distinct_counts = {col_name: df_develop_expanded.select(col_name).distinct().count() for col_name in df_develop_expanded.columns}

# Convert the dictionary to a DataFrame for better visualization
distinct_counts_df = spark.createDataFrame(distinct_counts.items(), ["Column", "Distinct_Count"])

# Display the distinct counts DataFrame
display(distinct_counts_df)

# COMMAND ----------

from pyspark.sql.functions import col

# Filter columns where distinct count is greater than 1
columns_to_keep = [row['Column'] for row in distinct_counts_df.collect() if row['Distinct_Count'] > 1]

# Select only the columns to keep from df_develop_expanded
df_develop_expanded_filtered = df_develop_expanded.select(*columns_to_keep)

# Display the filtered DataFrame
display(df_develop_expanded_filtered)

# COMMAND ----------

# Columns to drop
columns_to_drop = ["paginable", "summary", "total", "upperRangePosition", "totalPages"]

# Drop the specified columns from df_develop_expanded_filtered
df_develop_expanded_filtered = df_develop_expanded_filtered.drop(*columns_to_drop)

# Display the updated DataFrame
display(df_develop_expanded_filtered)

# COMMAND ----------

from pyspark.sql.functions import col, explode, split, regexp_extract

# Filter the DataFrame for the specific date
df_filtered_date = df_develop_expanded_filtered.filter(col("extracted_date") == "20240830114935")

# Extract keys and values from the 'element' column
df_exploded = df_filtered_date.withColumn("key_value", explode(split(col("element"), ",")))
df_key_value = df_exploded.withColumn("key", regexp_extract(col("key_value"), r"([^:]+):", 1)) \
                          .withColumn("value", regexp_extract(col("key_value"), r":(.+)", 1))

# Separate rows that appear after the key 'description' until another key appears
from pyspark.sql.window import Window
from pyspark.sql.functions import lag

window_spec = Window.partitionBy().orderBy("extracted_date")
df_key_value_with_lag = df_key_value.withColumn("prev_key", lag("key").over(window_spec))

df_description_rows = df_key_value_with_lag.filter((col("prev_key") == "description") & (col("key") != "description"))

# Display the results
display(df_key_value)
display(df_description_rows)

